;; Anything you type in here will be executed
;; immediately with the results shown on the
;; right.

(ns rna.core
  (:require [clojure.string :as s]))

(def input "")
(def table (load-file "/opt/apps/rosalind/rnatable.clj"))

(defn rna-to-protein [input table]
  (if (> (count input) 0)
    (let [el (table (keyword (s/join (take 3 input))))]
      (if (not (= "Stop" el))
        (s/join (cons el (rna-to-protein (drop 3 input) table)))))))


(rna-to-protein input table)